#include <iostream>
#include <vector>
#include <string>
#include <algorithm>

using namespace std;

int main() {
    int H, W, R, C;
    cin >> H >> W;
    vector<string> region(H);
    for (int i = 0; i < H; i++) {
        cin >> region[i];
    }
    cin >> R >> C;
    vector<string> pattern(R);
    for (int i = 0; i < R; i++) {
        cin >> pattern[i];
    }

    for (int i = 0; i <= H - R; i++) {
        for (int j = 0; j <= W - C; j++) {
            bool match = true;
            for (int k = 0; k < R; k++) {
                for (int l = 0; l < C; l++) {
                    if (region[i + k][j + l] != pattern[k][l]) {
                        match = false;
                        break;
                    }
                }
                if (!match) break;
            }
            if (match) {
                cout << i << " " << j << endl;
            }
        }
    }

    return 0;
}
