import java.util.Scanner;

public class PatternSearch {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);

        int H = scanner.nextInt();
        int W = scanner.nextInt();

        char[][] region = new char[H][W];
        for (int i = 0; i < H; i++) {
            String line = scanner.next();
            for (int j = 0; j < W; j++) {
                region[i][j] = line.charAt(j);
            }
        }

        int R = scanner.nextInt();
        int C = scanner.nextInt();

        char[][] pattern = new char[R][C];
        for (int i = 0; i < R; i++) {
            String line = scanner.next();
            for (int j = 0; j < C; j++) {
                pattern[i][j] = line.charAt(j);
            }
        }

        for (int i = 0; i <= H - R; i++) {
            for (int j = 0; j <= W - C; j++) {
                boolean found = true;
                for (int k = 0; k < R; k++) {
                    for (int l = 0; l < C; l++) {
                        if (region[i + k][j + l] != pattern[k][l]) {
                            found = false;
                            break;
                        }
                    }
                    if (!found) break;
                }
                if (found) {
                    System.out.println(i + " " + j);
                }
            }
        }
    }
}
