#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAX_N 100000

typedef struct {
    double value;
    double weight;
    double ratio;
} Item;

int compare_items(const void *a, const void *b) {
    Item *item_a = (Item *)a;
    Item *item_b = (Item *)b;
    return (item_b->ratio - item_a->ratio) > 0 ? 1 : -1;
}

int main() {
    int N, W;
    scanf("%d %d", &N, &W);

    Item *items = (Item *)malloc(N * sizeof(Item));
    for (int i = 0; i < N; i++) {
        scanf("%lf %lf", &items[i].value, &items[i].weight);
        items[i].ratio = items[i].value / items[i].weight;
    }

    qsort(items, N, sizeof(Item), compare_items);

    double total_value = 0.0;
    for (int i = 0; i < N; i++) {
        if (W >= items[i].weight) {
            W -= items[i].weight;
            total_value += items[i].value;
        } else {
            double fraction = (double)W / items[i].weight;
            total_value += items[i].value * fraction;
            break;
        }
    }

    printf("%.9lf\n", total_value);

    free(items);
    return 0;
}
