#include <stdio.h>
#include <stdlib.h>

typedef struct {
    int start;
    int end;
} Activity;

int compareActivities(const void *a, const void *b) {
    Activity *activityA = (Activity *)a;
    Activity *activityB = (Activity *)b;
    return activityA->end - activityB->end;
}

int main() {
    int n;
    scanf("%d", &n);

    Activity *activities = (Activity *)malloc(n * sizeof(Activity));
    for (int i = 0; i < n; i++) {
        scanf("%d %d", &activities[i].start, &activities[i].end);
    }

    qsort(activities, n, sizeof(Activity), compareActivities);

    int count = 1;
    int finishTime = activities[0].end;

    for (int i = 1; i < n; i++) {
        if (activities[i].start >= finishTime) {
            count++;
            finishTime = activities[i].end;
        }
    }

    printf("%d\n", count);

    free(activities);
    return 0;
}
