#include <iostream>
#include <vector>
#include <algorithm>

struct Activity {
    int start;
    int finish;
};

bool compareActivities(const Activity& a, const Activity& b) {
    return a.finish < b.finish;
}

int main() {
    int n;
    std::cin >> n;

    std::vector<Activity> activities(n);
    for (int i = 0; i < n; ++i) {
        std::cin >> activities[i].start >> activities[i].finish;
    }

    std::sort(activities.begin(), activities.end(), compareActivities);

    int count = 1;
    int finish = activities[0].finish;

    for (int i = 1; i < n; ++i) {
        if (activities[i].start >= finish) {
            count++;
            finish = activities[i].finish;
        }
    }

    std::cout << count << std::endl;

    return 0;
}
