#include <iostream>
#include <queue>
#include <unordered_map>
#include <string>

using namespace std;

struct Node {
    char c;
    int freq;
    Node* left;
    Node* right;
};

int huffmanCoding(string S) {
    unordered_map<char, int> freq;
    for (char c : S) {
        freq[c]++;
    }

    priority_queue<Node*, vector<Node*>, greater<Node*>> pq;
    for (auto& pair : freq) {
        Node* node = new Node();
        node->c = pair.first;
        node->freq = pair.second;
        node->left = nullptr;
        node->right = nullptr;
        pq.push(node);
    }

    while (pq.size() > 1) {
        Node* x = pq.top();
        pq.pop();
        Node* y = pq.top();
        pq.pop();

        Node* z = new Node();
        z->c = '\0';
        z->freq = x->freq + y->freq;
        z->left = x;
        z->right = y;

        pq.push(z);
    }

    unordered_map<char, string> codes;
    function<void(Node*, string)> dfs = [&](Node* node, string code) {
        if (node->c != '\0') {
            codes[node->c] = code;
        }
        if (node->left) {
            dfs(node->left, code + "0");
        }
        if (node->right) {
            dfs(node->right, code + "1");
        }
    };
    dfs(pq.top(), "");

    int length = 0;
    for (char c : S) {
        length += codes[c].length();
    }

    return length;
}

int main() {
    string S;
    cin >> S;
    cout << huffmanCoding(S) << endl;
    return 0;
}
