#include <iostream>
#include <vector>

void insertionSort(std::vector<int>& A) {
    for (int i = 1; i < A.size(); i++) {
        int key = A[i];
        int j = i - 1;
        std::cout << "Intermediate sequence: ";
        for (int k = 0; k < A.size(); k++) {
            if (k == i) {
                std::cout << "...";
            } else {
                std::cout << A[k] << " ";
            }
        }
        std::cout << std::endl;
        while (j >= 0 && A[j] > key) {
            A[j + 1] = A[j];
            j--;
        }
        A[j + 1] = key;
        std::cout << "Sorted sequence: ";
        for (int k = 0; k < A.size(); k++) {
            std::cout << A[k] << " ";
        }
        std::cout << std::endl;
    }
}

int main() {
    int N;
    std::cin >> N;
    std::vector<int> A(N);
    for (int i = 0; i < N; i++) {
        std::cin >> A[i];
    }
    insertionSort(A);
    return 0;
}
