#include <stdio.h>

void swap(int *a, int *b) {
    int temp = *a;
    *a = *b;
    *b = temp;
}

void selectionSort(int A[], int n) {
    int mini, i, j, swapCount = 0;
    for (i = 0; i < n; i++) {
        mini = i;
        for (j = i; j < n; j++) {
            if (A[j] < A[mini]) {
                mini = j;
            }
        }
        if (i != mini) {
            swap(&A[i], &A[mini]);
            swapCount++;
        }
    }
    printf("%d", A[0]);
    for (i = 1; i < n; i++) {
        printf(" %d", A[i]);
    }
    printf("\n%d\n", swapCount);
}

int main() {
    int n;
    scanf("%d", &n);
    int A[n];
    for (int i = 0; i < n; i++) {
        scanf("%d", &A[i]);
    }
    selectionSort(A, n);
    return 0;
}
