#include <iostream>
using namespace std;

void swap(int &a, int &b) {
    int temp = a;
    a = b;
    b = temp;
}

void selectionSort(int A[], int n) {
    int mini, swapCount = 0;
    for (int i = 0; i < n; i++) {
        mini = i;
        for (int j = i; j < n; j++) {
            if (A[j] < A[mini]) {
                mini = j;
            }
        }
        if (i != mini) {
            swap(A[i], A[mini]);
            swapCount++;
        }
    }
    cout << "Sorted sequence: ";
    for (int i = 0; i < n; i++) {
        cout << A[i] << " ";
    }
    cout << endl;
    cout << "Number of swap operations: " << swapCount << endl;
}

int main() {
    int n;
    cin >> n;
    int A[n];
    for (int i = 0; i < n; i++) {
        cin >> A[i];
    }
    selectionSort(A, n);
    return 0;
}
