#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAXN 100
#define MAXLEN 200

typedef struct Node {
    char val;
    struct Node* left;
    struct Node* right;
} Node;

Node* createNode(char val) {
    Node* node = (Node*)malloc(sizeof(Node));
    node->val = val;
    node->left = NULL;
    node->right = NULL;
    return node;
}

Node* parseTree(char* str) {
    Node* root = NULL;
    int len = strlen(str);
    int i = 0;
    while (i < len) {
        if (str[i] == '(') {
            i++;
            Node* node = createNode(str[i]);
            i++;
            if (str[i] == ',') {
                node->left = parseTree(&str[i + 1]);
                i += strlen(node->left->val) + 2;
            }
            if (str[i] == ',') {
                i++;
                node->right = parseTree(&str[i + 1]);
                i += strlen(node->right->val) + 2;
            }
            if (root == NULL) {
                root = node;
            } else {
                Node* cur = root;
                while (cur->right != NULL) {
                    cur = cur->right;
                }
                cur->right = node;
            }
        }
    }
    return root;
}

void printTree(Node* root) {
    if (root == NULL) {
        return;
    }
    printf("%c", root->val);
    if (root->left != NULL) {
        printf(",");
        printTree(root->left);
    }
    if (root->right != NULL) {
        printf(",");
        printTree(root->right);
    }
}

void intersection(Node* tree1, Node* tree2) {
    Node* root = NULL;
    Node* cur = tree1;
    while (cur != NULL) {
        Node* next = cur->right;
        while (next != NULL) {
            next = next->right;
        }
        if (next == NULL) {
            break;
        }
        if (next->val == tree2->val) {
            if (root == NULL) {
                root = cur;
            } else {
                Node* prev = root;
                while (prev->right != NULL) {
                    prev = prev->right;
                }
                prev->right = cur;
            }
        }
        cur = cur->right;
    }
    printTree(root);
    printf("\n");
}

void unionTree(Node* tree1, Node* tree2) {
    Node* root = NULL;
    Node* cur = tree1;
    while (cur != NULL) {
        Node* next = cur->right;
        while (next != NULL) {
            next = next->right;
        }
        if (next == NULL) {
            break;
        }
        if (root == NULL) {
            root = cur;
        } else {
            Node* prev = root;
            while (prev->right != NULL) {
                prev = prev->right;
            }
            prev->right = cur;
        }
        cur = cur->right;
    }
    cur = tree2;
    while (cur != NULL) {
        Node* next = cur->right;
        while (next != NULL) {
            next = next->right;
        }
        if (next == NULL) {
            break;
        }
        if (root == NULL) {
            root = cur;
        } else {
            Node* prev = root;
            while (prev->right != NULL) {
                prev = prev->right;
            }
            prev->right = cur;
        }
        cur = cur->right;
    }
    printTree(root);
    printf("\n");
}

int main() {
    char str1[MAXLEN], str2[MAXLEN];
    while (scanf("%c %s %s", &str1[0], str1 + 1, str2) == 3) {
        Node* tree1 = parseTree(str1);
        Node* tree2 = parseTree(str2);
        if (str1[0] == 'i') {
            intersection(tree1, tree2);
        } else {
            unionTree(tree1, tree2);
        }
    }
    return 0;
}
