#include <stdio.h>
#include <string.h>

typedef struct {
    char suit[2];
    int value;
} Card;

void bubbleSort(Card cards[], int n) {
    for (int i = 0; i < n - 1; i++) {
        for (int j = n - 1; j >= i + 1; j--) {
            if (cards[j].value < cards[j - 1].value) {
                Card temp = cards[j];
                cards[j] = cards[j - 1];
                cards[j - 1] = temp;
            }
        }
    }
}

void selectionSort(Card cards[], int n) {
    for (int i = 0; i < n - 1; i++) {
        int mini = i;
        for (int j = i + 1; j < n; j++) {
            if (cards[j].value < cards[mini].value) {
                mini = j;
            }
        }
        Card temp = cards[i];
        cards[i] = cards[mini];
        cards[mini] = temp;
    }
}

