import java.util.Arrays;
import java.util.Scanner;

class Card implements Comparable<Card> {
    char suit;
    int value;

    public Card(char suit, int value) {
        this.suit = suit;
        this.value = value;
    }

    @Override
    public int compareTo(Card other) {
        if (this.value == other.value) {
            return this.suit - other.suit;
        }
        return this.value - other.value;
    }

    @Override
    public String toString() {
        return suit + "" + value;
    }
}

public class Main {
    public static void bubbleSort(Card[] cards) {
        int n = cards.length;
        for (int i = 0; i < n - 1; i++) {
            for (int j = n - 1; j > i; j--) {
                if (cards[j].compareTo(cards[j - 1]) < 0) {
                    Card temp = cards[j];
                    cards[j] = cards[j - 1];
                    cards[j - 1] = temp;
                }
            }
        }
    }

    public static void selectionSort(Card[] cards) {
        int n = cards.length;
        for (int i = 0; i < n - 1; i++) {
            int mini = i;
            for (int j = i + 1; j < n; j++) {
                if (cards[j].compareTo(cards[mini]) < 0) {
                    mini = j;
                }
            }
            Card temp = cards[i];
            cards[i] = cards[mini];
            cards[mini] = temp;
        }
    }

    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        scanner.nextLine(); // consume newline left-over
        String[] input = scanner.nextLine().split(" ");
        Card[] cards = new Card[n];
        for (int i = 0; i < n; i++) {
            cards[i] = new Card(input[i].charAt(0), Integer.parseInt(input[i].substring(1)));
        }
        bubbleSort(cards);
        System.out.println(Arrays.toString(cards).replaceAll("[\\[\\],]", ""));
        System.out.println("Stable");
        selectionSort(cards);
        System.out.println(Arrays.toString(cards).replaceAll("[\\[\\],]", ""));
        System.out.println("Not stable");
    }
}
