class Card:
    def __init__(self, suit, value):
        self.suit = suit
        self.value = int(value)

    def __repr__(self):
        return f"{self.suit}{self.value}"

def bubble_sort(cards):
    n = len(cards)
    for i in range(n-1):
        for j in range(n-i-1):
            if cards[j].value > cards[j+1].value:
                cards[j], cards[j+1] = cards[j+1], cards[j]
    return cards

def selection_sort(cards):
    n = len(cards)
    for i in range(n-1):
        mini = i
        for j in range(i+1, n):
            if cards[j].value < cards[mini].value:
                mini = j
        cards[i], cards[mini] = cards[mini], cards[i]
    return cards

def main():
    n = int(input())
    cards = [Card(*card.split()) for card in input().split()]
    bubble_sorted_cards = bubble_sort(cards.copy())
    selection_sorted_cards = selection_sort(cards.copy())

    print(" ".join(map(str, bubble_sorted_cards)))
    print("Stable" if bubble_sorted_cards == cards else "Not stable")

    print(" ".join(map(str, selection_sorted_cards)))
    print("Stable" if selection_sorted_cards == cards else "Not stable")

if __name__ == "__main__":
    main()
