#include <stdio.h>
#include <stdlib.h>

#define MAX_N 1000000

void insertionSort(int A[], int n, int g) {
    int i, j, v, cnt = 0;
    for (i = g; i < n; i++) {
        v = A[i];
        j = i - g;
        while (j >= 0 && A[j] > v) {
            A[j + g] = A[j];
            j -= g;
            cnt++;
        }
        A[j + g] = v;
    }
}

void shellSort(int A[], int n) {
    int m, i, cnt = 0;
    int G[] = {1, 2, 4, 8, 16, 32, 64, 128, 256, 512, 1024, 2048, 4096, 8192, 16384, 32768, 65536, 131072, 262144, 524288, 1048576};
    m = sizeof(G) / sizeof(G[0]);
    for (i = 0; i < m; i++) {
        insertionSort(A, n, G[i]);
    }
    printf("%d\n", m);
    for (i = 0; i < m; i++) {
        printf("%d ", G[i]);
    }
    printf("\n%d\n", cnt);
    for (i = 0; i < n; i++) {
        printf("%d\n", A[i]);
    }
}

int main() {
    int n, i;
    scanf("%d", &n);
    int A[n];
    for (i = 0; i < n; i++) {
        scanf("%d", &A[i]);
    }
    shellSort(A, n);
    return 0;
}
