#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

void insertionSort(vector<int>& A, int n, int g) {
    int cnt = 0;
    for (int i = g; i < n; i++) {
        int v = A[i];
        int j = i - g;
        while (j >= 0 && A[j] > v) {
            A[j + g] = A[j];
            j -= g;
            cnt++;
        }
        A[j + g] = v;
    }
}

void shellSort(vector<int>& A, int n) {
    int cnt = 0;
    int m = 1;
    while (3 * m + 1 <= n) {
        m = 3 * m + 1;
    }
    vector<int> G;
    for (int i = m; i >= 1; i = (i - 1) / 3) {
        G.push_back(i);
    }
    for (int i = 0; i < G.size(); i++) {
        insertionSort(A, n, G[i]);
    }
    cout << G.size() << endl;
    for (int i = 0; i < G.size(); i++) {
        cout << G[i] << " ";
    }
    cout << endl;
    cout << cnt << endl;
    for (int i = 0; i < n; i++) {
        cout << A[i] << endl;
    }
}

int main() {
    int n;
    cin >> n;
    vector<int> A(n);
    for (int i = 0; i < n; i++) {
        cin >> A[i];
    }
    shellSort(A, n);
    return 0;
}
