import java.util.Arrays;
import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        int[] A = new int[n];
        for (int i = 0; i < n; i++) {
            A[i] = scanner.nextInt();
        }
        int m = 1;
        while (3 * m + 1 <= n) {
            m = 3 * m + 1;
        }
        while (m-- > 0) {
            int[] G = new int[m];
            for (int i = 0; i < m; i++) {
                G[i] = (int) Math.pow(2, m - 1 - i);
            }
            int cnt = 0;
            insertionSort(A, n, G);
            System.out.println(m);
            for (int i = 0; i < m; i++) {
                System.out.print(G[i] + " ");
            }
            System.out.println();
            System.out.println(cnt);
        }
        Arrays.sort(A);
        for (int i = 0; i < n; i++) {
            System.out.println(A[i]);
        }
    }

    public static void insertionSort(int[] A, int n, int[] G) {
        for (int i = G[0]; i < n; i++) {
            int v = A[i];
            int j = i - G[0];
            while (j >= 0 && A[j] > v) {
                A[j + G[0]] = A[j];
                j -= G[0];
            }
            A[j + G[0]] = v;
        }
    }
}
