#include <iostream>
#include <queue>
#include <string>

struct Process {
    std::string name;
    int time;
    int finishTime;
};

int main() {
    int n, q;
    std::cin >> n >> q;

    std::queue<Process> queue;
    for (int i = 0; i < n; i++) {
        std::string name;
        int time;
        std::cin >> name >> time;
        queue.push({name, time, 0});
    }

    while (!queue.empty()) {
        Process process = queue.front();
        queue.pop();

        int timeHandled = std::min(q, process.time);
        process.time -= timeHandled;
        process.finishTime += timeHandled;

        if (process.time > 0) {
            queue.push(process);
        } else {
            std::cout << process.name << " " << process.finishTime << std::endl;
        }
    }

    return 0;
}
