import sys

class Process:
    def __init__(self, name, time):
        self.name = name
        self.time = time
        self.finish_time = 0

def round_robin(n, q, processes):
    queue = processes[:]
    while queue:
        process = queue.pop(0)
        time_left = min(q, process.time)
        process.time -= time_left
        process.finish_time += time_left
        if process.time > 0:
            queue.append(process)
        else:
            print(f"{process.name} {process.finish_time}")

def main():
    n, q = map(int, input().split())
    processes = []
    for _ in range(n):
        name, time = input().split()
        processes.append(Process(name, int(time)))
    round_robin(n, q, processes)

if __name__ == "__main__":
    main()
