import java.util.*;
import java.io.*;

public class Main {
    static class Node {
        String left;
        String right;

        public Node(String left, String right) {
            this.left = left;
            this.right = right;
        }
    }

    static Node parse(String s) {
        int n = s.length();
        int i = 0;
        while (i < n && s.charAt(i) != '(') i++;
        if (i == n) return new Node("", "");
        int j = i + 1;
        while (j < n && s.charAt(j) != ',') j++;
        String left = s.substring(i + 1, j);
        i = j + 1;
        while (i < n && s.charAt(i) != ')') i++;
        String right = s.substring(j + 1, i);
        return new Node(left, right);
    }

    static String intersect(Node a, Node b) {
        if (a.left.equals("") && a.right.equals("")) return "";
        if (b.left.equals("") && b.right.equals("")) return "";
        if (a.left.equals(b.left)) return intersect(new Node(a.left, a.right), b);
        if (a.right.equals(b.right)) return intersect(a, new Node(b.left, b.right));
        return "(" + a.left + "," + intersect(a.right, b) + ")";
    }

    static String union(Node a, Node b) {
        if (a.left.equals("") && a.right.equals("")) return b.left + b.right;
        if (b.left.equals("") && b.right.equals("")) return a.left + a.right;
        return "(" + a.left + "," + union(a.right, b) + ")";
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        String line;
        while ((line = br.readLine()) != null) {
            char op = line.charAt(0);
            String[] trees = line.substring(2).split(" ");
            Node a = parse(trees[0]);
            Node b = parse(trees[1]);
            String result;
            if (op == 'i') result = intersect(a, b);
            else result = union(a, b);
            System.out.println(result);
        }
    }
}
