import java.util.ArrayList;
import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        String s = scanner.next();
        int n = s.length();
        int[] left = new int[n];
        int[] right = new int[n];
        int[] maxLeft = new int[n];
        int[] maxRight = new int[n];
        int[] maxArea = new int[n];
        int[] area = new int[n];
        int[] flood = new int[n];
        int[] floodArea = new int[n];
        int floodCount = 0;
        int maxAreaIndex = 0;
        int maxAreaCount = 0;

        for (int i = 0; i < n; i++) {
            if (s.charAt(i) == '\\') {
                left[i] = 1;
                right[i] = 1;
            } else if (s.charAt(i) == '/') {
                left[i] = 0;
                right[i] = 0;
            } else {
                left[i] = 0;
                right[i] = 1;
            }
        }

        for (int i = 0; i < n; i++) {
            maxLeft[i] = left[i];
            maxRight[i] = right[i];
            maxArea[i] = left[i] * right[i];
            area[i] = left[i] * right[i];
            if (i > 0) {
                maxLeft[i] = Math.max(maxLeft[i], maxLeft[i - 1]);
                maxRight[i] = Math.max(maxRight[i], maxRight[i - 1]);
                maxArea[i] = Math.max(maxArea[i], maxArea[i - 1]);
                area[i] = Math.max(area[i], area[i - 1]);
            }
        }

        for (int i = n - 1; i >= 0; i--) {
            if (left[i] == 1 && right[i] == 1) {
                flood[floodCount] = i;
                floodArea[floodCount] = area[i];
                floodCount++;
            }
        }

        int totalArea = 0;
        for (int i = 0; i < floodCount; i++) {
            totalArea += floodArea[i];
        }

        System.out.println(totalArea);
        System.out.print(floodCount + " ");
        for (int i = 0; i < floodCount; i++) {
            System.out.print(floodArea[i] + " ");
        }
    }
}
