#include <iostream>
#include <unordered_set>

int main() {
    int n;
    std::cin >> n;

    std::unordered_set<int> S;
    for (int i = 0; i < n; ++i) {
        int x;
        std::cin >> x;
        S.insert(x);
    }

    int q;
    std::cin >> q;

    int C = 0;
    for (int i = 0; i < q; ++i) {
        int x;
        std::cin >> x;
        if (S.find(x) != S.end()) {
            C++;
        }
    }

    std::cout << C << std::endl;

    return 0;
}
