#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAX_N 100000

int main() {
    int n, k;
    scanf("%d %d", &n, &k);

    int w[MAX_N];
    for (int i = 0; i < n; i++) {
        scanf("%d", &w[i]);
    }

    int l = 0;
    int r = 0;
    for (int i = 0; i < n; i++) {
        r += w[i];
    }

    while (l < r) {
        int mid = (l + r) / 2;
        int sum = 0;
        int cnt = 0;
        for (int i = 0; i < n; i++) {
            sum += w[i];
            if (sum > mid) {
                sum = w[i];
                cnt++;
            }
            if (sum == mid || i == n - 1) {
                if (sum > mid) cnt++;
                sum = 0;
            }
        }
        if (cnt <= k) {
            r = mid;
        } else {
            l = mid + 1;
        }
    }

    printf("%d\n", l);

    return 0;
}
