#include <iostream>
#include <vector>
#include <algorithm>

int main() {
    int n, k;
    std::cin >> n >> k;

    std::vector<int> w(n);
    for (int i = 0; i < n; ++i) {
        std::cin >> w[i];
    }

    std::sort(w.begin(), w.end());

    int l = 0, r = 0;
    for (int i = 0; i < n; ++i) {
        r += w[i];
    }

    while (l < r) {
        int mid = (l + r) / 2;
        int sum = 0, cnt = 0;
        for (int i = 0; i < n; ++i) {
            sum += w[i];
            if (sum > mid) {
                cnt += 1;
                sum = w[i];
            }
        }
        cnt += 1;

        if (cnt > k) {
            l = mid + 1;
        } else {
            r = mid;
        }
    }

    std::cout << l << std::endl;

    return 0;
}
