import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        int[] A = new int[n];
        for (int i = 0; i < n; i++) {
            A[i] = scanner.nextInt();
        }
        int q = scanner.nextInt();
        int[] M = new int[q];
        for (int i = 0; i < q; i++) {
            M[i] = scanner.nextInt();
        }
        for (int i = 0; i < q; i++) {
            System.out.println(solve(A, M[i], 0) ? "yes" : "no");
        }
    }

    public static boolean solve(int[] A, int M, int p) {
        if (M == 0) return true;
        if (p == A.length || M < 0) return false;
        return solve(A, M - A[p], p + 1) || solve(A, M, p + 1);
    }
}
