#include <iostream>
#include <vector>

void Merge(std::vector<int>& A, int left, int mid, int right, int& comparisons) {
    int n1 = mid - left;
    int n2 = right - mid;
    std::vector<int> L(n1 + 1);
    std::vector<int> R(n2 + 1);
    for (int i = 0; i < n1; i++) {
        L[i] = A[left + i];
    }
    for (int i = 0; i < n2; i++) {
        R[i] = A[mid + i];
    }
    L[n1] = 1e9 + 1; // sentinel value
    R[n2] = 1e9 + 1; // sentinel value
    int i = 0;
    int j = 0;
    for (int k = left; k < right; k++) {
        comparisons++;
        if (L[i] <= R[j]) {
            A[k] = L[i];
            i++;
        } else {
            A[k] = R[j];
            j++;
        }
    }
}

void MergeSort(std::vector<int>& A, int left, int right, int& comparisons) {
    if (left + 1 < right) {
        int mid = (left + right) / 2;
        MergeSort(A, left, mid, comparisons);
        MergeSort(A, mid, right, comparisons);
        Merge(A, left, mid, right, comparisons);
    }
}

int main() {
    int n;
    std::cin >> n;
    std::vector<int> A(n);
    for (int i = 0; i < n; i++) {
        std::cin >> A[i];
    }
    int comparisons = 0;
    MergeSort(A, 0, n, comparisons);
    for (int i = 0; i < n; i++) {
        std::cout << A[i] << " ";
    }
    std::cout << std::endl;
    std::cout << comparisons << std::endl;
    return 0;
}
