#include <stdio.h>
#include <math.h>

void draw_koch_curve(double x1, double y1, double x2, double y2, int n) {
    if (n == 0) {
        printf("%.10f %.10f\n", (x1 + x2) / 2, (y1 + y2) / 2);
        printf("%.10f %.10f\n", (x1 + x2) / 2 + (x2 - x1) / 3, (y1 + y2) / 2 + (y2 - y1) / 3 * sqrt(3));
        printf("%.10f %.10f\n", (x1 + x2) / 2 + 2 * (x2 - x1) / 3, (y1 + y2) / 2 + 2 * (y2 - y1) / 3 * sqrt(3));
    } else {
        double x3 = (x1 + 2 * x2) / 3;
        double y3 = (y1 + 2 * y2) / 3;
        double x4 = (x1 + x2) / 3 + (x2 - x1) / 3 * sqrt(3);
        double y4 = (y1 + y2) / 3 + (y2 - y1) / 3 * sqrt(3);
        double x5 = (x1 + x2) / 3 + 2 * (x2 - x1) / 3 * sqrt(3);
        double y5 = (y1 + y2) / 3 + 2 * (y2 - y1) / 3 * sqrt(3);
        draw_koch_curve(x1, y1, x3, y3, n - 1);
        draw_koch_curve(x3, y3, x4, y4, n - 1);
        draw_koch_curve(x4, y4, x5, y5, n - 1);
        draw_koch_curve(x5, y5, x2, y2, n - 1);
    }
}

int main() {
    int n;
    scanf("%d", &n);
    draw_koch_curve(0, 0, 100, 0, n);
    return 0;
}
