#include <iostream>
#include <cmath>

void drawKochCurve(int n, double x1, double y1, double x2, double y2) {
    if (n == 0) {
        std::cout << x1 << " " << y1 << std::endl;
        std::cout << x2 << " " << y2 << std::endl;
    } else {
        double x3 = (2 * x1 + x2) / 3;
        double y3 = (2 * y1 + y2) / 3;
        double x4 = (x1 + x2) / 2 + (y2 - y1) * sqrt(3) / 6;
        double y4 = (y1 + y2) / 2 + (x2 - x1) * sqrt(3) / 6;
        double x5 = (x1 + x2) / 2 - (y2 - y1) * sqrt(3) / 6;
        double y5 = (y1 + y2) / 2 - (x2 - x1) * sqrt(3) / 6;

        drawKochCurve(n - 1, x1, y1, x3, y3);
        drawKochCurve(n - 1, x3, y3, x4, y4);
        drawKochCurve(n - 1, x4, y4, x5, y5);
        drawKochCurve(n - 1, x5, y5, x2, y2);
    }
}

int main() {
    int n;
    std::cin >> n;
    drawKochCurve(n, 0, 0, 100, 0);
    return 0;
}
