public class KochCurve {
    public static void main(String[] args) {
        int n = Integer.parseInt(args[0]);
        kochCurve(0, 100, 0, n);
    }

    public static void kochCurve(double x1, double y1, double x2, double y2, int n) {
        if (n == 0) {
            System.out.println(x1 + " " + y1);
            System.out.println(x2 + " " + y2);
        } else {
            double x3 = (2 * x1 + x2) / 3;
            double y3 = (2 * y1 + y2) / 3;
            double x4 = (x1 + 2 * x2) / 3;
            double y4 = (y1 + 2 * y2) / 3;
            double x5 = (x1 + x2) / 2 + (x2 - x1) / 3 * Math.sin(Math.PI / 3);
            double y5 = (y1 + y2) / 2 + (y2 - y1) / 3 * Math.sin(Math.PI / 3);
            double x6 = (x1 + x2) / 2 + (x2 - x1) / 3 * Math.cos(Math.PI / 3);
            double y6 = (y1 + y2) / 2 + (y2 - y1) / 3 * Math.cos(Math.PI / 3);

            kochCurve(x1, y1, x3, y3, n - 1);
            kochCurve(x3, y3, x5, y5, n - 1);
            kochCurve(x5, y5, x6, y6, n - 1);
            kochCurve(x6, y6, x4, y4, n - 1);
            kochCurve(x4, y4, x2, y2, n - 1);
        }
    }
}
