#include <stdio.h>
#include <stdlib.h>

int merge(int* arr, int l, int r) {
    int mid = (l + r) / 2;
    int i = l, j = mid + 1, k = l, inv = 0;
    int* temp = (int*)malloc((r - l + 1) * sizeof(int));

    while (i <= mid && j <= r) {
        if (arr[i] <= arr[j]) {
            temp[k++] = arr[i++];
        } else {
            temp[k++] = arr[j++];
            inv += mid - i + 1;
        }
    }

    while (i <= mid) {
        temp[k++] = arr[i++];
    }

    while (j <= r) {
        temp[k++] = arr[j++];
    }

    for (i = l; i <= r; i++) {
        arr[i] = temp[i];
    }

    free(temp);
    return inv;
}

int mergeSort(int* arr, int l, int r) {
    int inv = 0;
    if (l < r) {
        int mid = (l + r) / 2;
        inv += mergeSort(arr, l, mid);
        inv += mergeSort(arr, mid + 1, r);
        inv += merge(arr, l, r);
    }
    return inv;
}

int main() {
    int n;
    scanf("%d", &n);
    int* arr = (int*)malloc(n * sizeof(int));
    for (int i = 0; i < n; i++) {
        scanf("%d", &arr[i]);
    }

    int inv = mergeSort(arr, 0, n - 1);
    printf("%d\n", inv);

    free(arr);
    return 0;
}
