import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int t = scanner.nextInt();
        scanner.nextLine();

        for (int i = 0; i < t; i++) {
            int n = scanner.nextInt();
            scanner.nextLine();
            String s = scanner.nextLine();

            int[][] dp = new int[4 * n + 1][2];
            for (int j = 0; j <= 4 * n; j++) {
                dp[j][0] = 0;
                dp[j][1] = 0;
            }

            for (int j = 1; j <= 4 * n; j++) {
                if (s.charAt(j - 1) == 'Y') {
                    dp[j][0] = dp[j - 1][0] + 1;
                    dp[j][1] = dp[j - 1][1] + 0.5;
                } else {
                    dp[j][0] = dp[j - 1][0];
                    dp[j][1] = dp[j - 1][1];
                }

                if (j % 4 == 0) {
                    dp[j][0] = Math.max(dp[j][0], dp[j - 1][0]);
                    dp[j][1] = Math.max(dp[j][1], dp[j - 1][1]);
                }
            }

            System.out.println((int) Math.ceil(dp[4 * n][1]));
        }
    }
}
