#include <iostream>
#include <vector>
#include <string>
#include <algorithm>

struct Card {
    std::string suit;
    int number;
};

bool compareCards(const Card& a, const Card& b) {
    if (a.number == b.number) {
        return a.suit < b.suit;
    }
    return a.number < b.number;
}

void partition(std::vector<Card>& cards, int p, int r) {
    int x = cards[r].number;
    int i = p - 1;
    for (int j = p; j < r; j++) {
        if (cards[j].number <= x) {
            i++;
            std::swap(cards[i], cards[j]);
        }
    }
    std::swap(cards[i + 1], cards[r]);
}

void quicksort(std::vector<Card>& cards, int p, int r) {
    if (p < r) {
        int q = partition(cards, p, r);
        quicksort(cards, p, q - 1);
        quicksort(cards, q + 1, r);
    }
}

int main() {
    int n;
    std::cin >> n;
    std::vector<Card> cards(n);
    for (int i = 0; i < n; i++) {
        std::cin >> cards[i].suit >> cards[i].number;
    }

    quicksort(cards, 0, n - 1);

    bool stable = true;
    for (int i = 0; i < n - 1; i++) {
        if (cards[i].number == cards[i + 1].number && cards[i].suit > cards[i + 1].suit) {
            stable = false;
            break;
        }
    }

    std::cout << (stable ? "Stable" : "Not stable") << std::endl;
    for (const auto& card : cards) {
        std::cout << card.suit << " " << card.number << std::endl;
    }

    return 0;
}
