import java.util.Arrays;
import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        Card[] cards = new Card[n];
        for (int i = 0; i < n; i++) {
            String[] input = scanner.next().split(" ");
            cards[i] = new Card(input[0], Integer.parseInt(input[1]));
        }
        quickSort(cards, 0, n - 1);
        boolean isStable = isStable(cards);
        System.out.println(isStable ? "Stable" : "Not stable");
        for (Card card : cards) {
            System.out.println(card);
        }
    }

    public static void quickSort(Card[] cards, int p, int r) {
        if (p < r) {
            int q = partition(cards, p, r);
            quickSort(cards, p, q - 1);
            quickSort(cards, q + 1, r);
        }
    }

    public static int partition(Card[] cards, int p, int r) {
        int x = cards[r].number;
        int i = p - 1;
        for (int j = p; j < r; j++) {
            if (cards[j].number <= x) {
                i++;
                swap(cards, i, j);
            }
        }
        swap(cards, i + 1, r);
        return i + 1;
    }

    public static void swap(Card[] cards, int i, int j) {
        Card temp = cards[i];
        cards[i] = cards[j];
        cards[j] = temp;
    }

    public static boolean isStable(Card[] cards) {
        for (int i = 0; i < cards.length - 1; i++) {
            if (cards[i].number == cards[i + 1].number && cards[i].suit.compareTo(cards[i + 1].suit) > 0) {
                return false;
            }
        }
        return true;
    }

    static class Card {
        String suit;
        int number;

        public Card(String suit, int number) {
            this.suit = suit;
            this.number = number;
        }

        @Override
        public String toString() {
            return suit + " " + number;
        }
    }
}
