def partition(arr, p, r):
    x = arr[r]
    i = p-1
    for j in range(p, r):
        if arr[j][1] <= x[1]:
            i += 1
            arr[i], arr[j] = arr[j], arr[i]
    arr[i+1], arr[r] = arr[r], arr[i+1]
    return i+1

def quicksort(arr, p, r):
    if p < r:
        q = partition(arr, p, r)
        quicksort(arr, p, q-1)
        quicksort(arr, q+1, r)

def main():
    n = int(input())
    arr = []
    for _ in range(n):
        suit, value = input().split()
        arr.append((suit, int(value)))

    quicksort(arr, 0, n-1)

    if all(arr[i][1] == arr[i+1][1] and arr[i][0] <= arr[i+1][0] for i in range(n-1)):
        print("Stable")
    else:
        print("Not stable")

    for card in arr:
        print(*card)

if __name__ == "__main__":
    main()
