#include <iostream>
#include <vector>
#include <algorithm>

int minCostSort(int n, std::vector<int>& arr) {
    std::vector<int> sortedArr = arr;
    std::sort(sortedArr.begin(), sortedArr.end());
    int cost = 0;
    for (int i = 0; i < n; i++) {
        if (arr[i] != sortedArr[i]) {
            int temp = arr[i];
            arr[i] = arr[std::lower_bound(sortedArr.begin(), sortedArr.end(), arr[i]) - sortedArr.begin()];
            arr[std::lower_bound(sortedArr.begin(), sortedArr.end(), temp) - sortedArr.begin()] = temp;
            cost += arr[i] + temp;
        }
    }
    return cost;
}

int main() {
    int n;
    std::cin >> n;
    std::vector<int> arr(n);
    for (int i = 0; i < n; i++) {
        std::cin >> arr[i];
    }
    std::cout << minCostSort(n, arr) << std::endl;
    return 0;
}
