#include <iostream>
#include <vector>
#include <string>

using namespace std;

struct Node {
    int id;
    int parent;
    int depth;
    string type;
    vector<int> children;
};

int main() {
    int n;
    cin >> n;

    vector<Node> nodes(n);
    for (int i = 0; i < n; i++) {
        cin >> nodes[i].id;
        int k;
        cin >> k;
        nodes[i].children.resize(k);
        for (int j = 0; j < k; j++) {
            cin >> nodes[i].children[j];
        }
    }

    nodes[0].parent = -1;
    nodes[0].depth = 0;
    nodes[0].type = "root";

    for (int i = 1; i < n; i++) {
        nodes[i].parent = nodes[nodes[i].children[0]].parent;
        nodes[i].depth = nodes[nodes[i].parent].depth + 1;
        if (nodes[i].parent == -1) {
            nodes[i].type = "root";
        } else if (nodes[i].children.size() == 1) {
            nodes[i].type = "leaf";
        } else {
            nodes[i].type = "internal node";
        }
    }

    for (int i = 0; i < n; i++) {
        cout << "node " << nodes[i].id << ": parent = " << nodes[i].parent << ", depth = " << nodes[i].depth << ", " << nodes[i].type;
        if (nodes[i].children.size() == 0) {
            cout << ", []";
        } else {
            cout << ", [";
            for (int j = 0; j < nodes[i].children.size(); j++) {
                cout << nodes[i].children[j];
                if (j < nodes[i].children.size() - 1) {
                    cout << ", ";
                }
            }
            cout << "]";
        }
        cout << endl;
    }

    return 0;
}
