#include <stdio.h>
#include <stdlib.h>

typedef struct {
    char *str;
    int len;
} Button;

Button buttons[] = {
    {"", 0},  // 0
    {" ", 1},  // 1
    {",", 2},  // 2
    {"A", 3},  // 3
    {"B", 4},  // 4
    {"C", 5},  // 5
    {"D", 6},  // 6
    {"E", 7},  // 7
    {"F", 8},  // 8
    {" ", 1},  // 9
    {" ", 0}   // 0 (special)
};

int main() {
    char input[10001];
    int i, j, k, n, m, count;
    Button *p;

    while (fgets(input, 10001, stdin) != NULL) {
        for (i = 0; input[i] != '\n' && input[i] != '\0'; i++) {
            count = 0;
            p = &buttons[input[i] - '0'];
            for (j = 0; j < p->len; j++) {
                if (count == 0) {
                    printf("%c", p->str[j]);
                }
                count = (count + 1) % 6;
            }
            if (p->len == 0 && count == 0) {
                for (k = 0; k < i; k++) {
                    printf(" ");
                }
            }
        }
        printf("\n");
    }

    return 0;
}
