class Node:
    def __init__(self, id, left=-1, right=-1):
        self.id = id
        self.left = left
        self.right = right

def build_tree(n, nodes):
    tree = [None] * n
    for i, node in enumerate(nodes):
        tree[node.id] = Node(node.id, node.left, node.right)
    return tree

def preorder(tree, node_id):
    if node_id == -1:
        return []
    node = tree[node_id]
    return [node.id] + preorder(tree, node.left) + preorder(tree, node.right)

def inorder(tree, node_id):
    if node_id == -1:
        return []
    node = tree[node_id]
    return inorder(tree, node.left) + [node.id] + inorder(tree, node.right)

def postorder(tree, node_id):
    if node_id == -1:
        return []
    node = tree[node_id]
    return postorder(tree, node.left) + postorder(tree, node.right) + [node.id]

def main():
    n = int(input())
    nodes = []
    for _ in range(n):
        id, left, right = map(int, input().split())
        nodes.append((Node(id, left, right)))
    tree = build_tree(n, nodes)

    print("Preorder")
    print(" ".join(map(str, preorder(tree, 0))))
    print("Inorder")
    print(" ".join(map(str, inorder(tree, 0))))
    print("Postorder")
    print(" ".join(map(str, postorder(tree, 0))))

if __name__ == "__main__":
    main()
