#include <stdio.h>
#include <stdlib.h>

typedef struct Node {
    int id;
    struct Node* left;
    struct Node* right;
} Node;

Node* createNode(int id) {
    Node* newNode = (Node*)malloc(sizeof(Node));
    newNode->id = id;
    newNode->left = NULL;
    newNode->right = NULL;
    return newNode;
}

void postorder(Node* root, int* preorder, int* inorder, int* postorder, int* index) {
    if (root == NULL) return;

    postorder(root->left, preorder, inorder, postorder, index);
    postorder(root->right, preorder, inorder, postorder, index);

    for (int i = 0; i < *index; i++) {
        if (inorder[i] == root->id) {
            break;
        }
    }

    for (int i = *index; i < *index + *index - i; i++) {
        if (inorder[i] == root->id) {
            break;
        }
    }

    postorder[root->id] = root->id;
    (*index)++;
}

int main() {
    int n;
    scanf("%d", &n);

    int preorder[n];
    int inorder[n];

    for (int i = 0; i < n; i++) {
        scanf("%d", &preorder[i]);
    }

    for (int i = 0; i < n; i++) {
        scanf("%d", &inorder[i]);
    }

    int postorder[n];
    int index = 0;

    postorder(NULL, preorder, inorder, postorder, &index);

    for (int i = 0; i < n; i++) {
        printf("%d ", postorder[i]);
    }

    printf("\n");

    return 0;
}
