#include <iostream>
#include <vector>

int main() {
    int n;
    std::cin >> n;

    std::vector<int> preorder(n);
    std::vector<int> inorder(n);

    for (int i = 0; i < n; ++i) {
        std::cin >> preorder[i];
    }

    for (int i = 0; i < n; ++i) {
        std::cin >> inorder[i];
    }

    std::vector<int> postorder(n);
    std::vector<int> index(n);

    for (int i = 0; i < n; ++i) {
        index[inorder[i] - 1] = i;
    }

    int root = preorder[0];
    int i = 0;
    int j = 0;

    while (i < n) {
        if (inorder[index[i]] == preorder[j]) {
            postorder[n - 1 - j] = preorder[j];
            j++;
            i++;
        } else {
            postorder[n - 1 - j] = preorder[i];
            i++;
        }
    }

    for (int i = 0; i < n; ++i) {
        std::cout << postorder[i] << " ";
    }

    return 0;
}
