import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        int[] preorder = new int[n];
        int[] inorder = new int[n];
        for (int i = 0; i < n; i++) {
            preorder[i] = scanner.nextInt();
        }
        for (int i = 0; i < n; i++) {
            inorder[i] = scanner.nextInt();
        }
        int[] postorder = new int[n];
        int[] index = new int[n];
        for (int i = 0; i < n; i++) {
            index[inorder[i] - 1] = i;
        }
        int root = preorder[0];
        int i = 0;
        int j = 0;
        while (i < n) {
            if (inorder[index[i]] == preorder[j]) {
                int k = index[i];
                while (k >= 0) {
                    postorder[n - j - 1] = inorder[k];
                    k = index[k];
                    j++;
                }
                i++;
            } else {
                j++;
            }
        }
        for (int x : postorder) {
            System.out.print(x + " ");
        }
    }
}
