#include <iostream>
#include <string>
#include <unordered_map>

using namespace std;

int main() {
    unordered_map<int, string> buttonMap = {
        {1, "()"},
        {2, "abcABC"},
        {3, "defDEF"},
        {4, "ghiGHI"},
        {5, "jklJKL"},
        {6, "mnoMNO"},
        {7, "pqrsPQRS"},
        {8, "tuvTUV"},
        {9, "wxyzWXYZ"},
        {0, " "}
    };

    string input;
    while (getline(cin, input)) {
        string output;
        int count = 1;
        for (int i = 0; i < input.length(); i++) {
            int num = input[i] - '0';
            if (i + 1 < input.length() && input[i + 1] - '0' == num) {
                count++;
            } else {
                if (num == 0) {
                    output += string(count - 1, ' ');
                } else {
                    output += buttonMap[num][count - 1];
                }
                count = 1;
            }
        }
        cout << output << endl;
    }

    return 0;
}
