#include <stdio.h>
#include <stdlib.h>

typedef struct Node {
    int key;
    struct Node* left;
    struct Node* right;
} Node;

Node* root = NULL;

void insert(int key) {
    Node* new_node = (Node*)malloc(sizeof(Node));
    new_node->key = key;
    new_node->left = NULL;
    new_node->right = NULL;

    if (root == NULL) {
        root = new_node;
    } else {
        Node* current = root;
        while (1) {
            if (key < current->key) {
                if (current->left == NULL) {
                    current->left = new_node;
                    break;
                }
                current = current->left;
            } else {
                if (current->right == NULL) {
                    current->right = new_node;
                    break;
                }
                current = current->right;
            }
        }
    }
}

int find(int key) {
    Node* current = root;
    while (current != NULL) {
        if (key == current->key) {
            return 1;
        } else if (key < current->key) {
            current = current->left;
        } else {
            current = current->right;
        }
    }
    return 0;
}

void delete(int key) {
    Node* current = root;
    Node* parent = NULL;
    while (current != NULL) {
        if (key == current->key) {
            break;
        } else if (key < current->key) {
            parent = current;
            current = current->left;
        } else {
            parent = current;
            current = current->right;
        }
    }

    if (current == NULL) {
        return;
    }

    if (current->left == NULL && current->right == NULL) {
        if (parent == NULL) {
            root = NULL;
        } else if (parent->left == current) {
            parent->left = NULL;
        } else {
            parent->right = NULL;
        }
        free(current);
    } else if (current->left == NULL) {
        if (parent == NULL) {
            root = current->right;
        } else if (parent->left == current) {
            parent->left = current->right;
        } else {
            parent->right = current->right;
        }
        free(current);
    } else if (current->right == NULL) {
        if (parent == NULL) {
            root = current->left;
        } else if (parent->left == current) {
            parent->left = current->left;
        } else {
            parent->right = current->left;
        }
        free(current);
    } else {
        Node* successor = current->right;
        Node* successor_parent = current;
        while (successor->left != NULL) {
            successor_parent = successor;
            successor = successor->left;
        }

        current->key = successor->key;
        if (successor_parent == current) {
            current->right = successor->right;
        } else {
            successor_parent->left = successor->right;
        }
        if (parent == NULL) {
            root = successor;
        } else if (parent->left == current) {
            parent->left = successor;
        } else {
            parent->right = successor;
        }
        free(successor);
    }
}

void inorder(Node* node) {
    if (node != NULL) {
        inorder(node->left);
        printf("%d ", node->key);
        inorder(node->right);
    }
}

void preorder(Node* node) {
    if (node != NULL) {
        printf("%d ", node->key);
        preorder(node->left);
        preorder(node->right);
    }
}

