#include <iostream>
#include <cstdlib>
#include <ctime>

using namespace std;

struct Node {
    int key, priority;
    Node* left, *right;
};

Node* createNode(int key, int priority) {
    Node* newNode = new Node;
    newNode->key = key;
    newNode->priority = priority;
    newNode->left = newNode->right = NULL;
    return newNode;
}

Node* rightRotate(Node* t) {
    Node* s = t->left;
    t->left = s->right;
    s->right = t;
    return s;
}

Node* leftRotate(Node* t) {
    Node* s = t->right;
    t->right = s->left;
    s->left = t;
    return s;
}

Node* insert(Node* t, int key, int priority) {
    if (t == NULL)
        return createNode(key, priority);
    if (key < t->key)
        t->left = insert(t->left, key, priority);
    else if (key > t->key)
        t->right = insert(t->right, key, priority);
    if (t->priority < t->left->priority)
        t = rightRotate(t);
    if (t->priority < t->right->priority)
        t = leftRotate(t);
    return t;
}

Node* deleteNode(Node* t, int key) {
    if (t == NULL)
        return t;
    if (key < t->key)
        t->left = deleteNode(t->left, key);
    else if (key > t->key)
        t->right = deleteNode(t->right, key);
    else {
        if (t->left == NULL)
            return t->right;
        else if (t->right == NULL)
            return t->left;
        else if (t->left->priority > t->right->priority)
            t = rightRotate(t);
        else
            t = leftRotate(t);
        t->left = deleteNode(t->left, key);
    }
    return t;
}

void inorder(Node* t, int& count) {
    if (t != NULL) {
        inorder(t->left, count);
        cout << t->key << " ";
        inorder(t->right, count);
    }
}

void preorder(Node* t) {
    if (t != NULL) {
        cout << t->key << " ";
        preorder(t->left);
        preorder(t->right);
    }
}

