#include <iostream>
#include <vector>

int parent(int i) {
    return i / 2;
}

int left(int i) {
    return 2 * i;
}

int right(int i) {
    return 2 * i + 1;
}

int main() {
    int H;
    std::cin >> H;

    std::vector<int> A(H + 1);
    for (int i = 1; i <= H; ++i) {
        std::cin >> A[i];
    }

    for (int i = 1; i <= H; ++i) {
        std::cout << "node " << i << ": key = " << A[i];
        if (i > 1) {
            std::cout << ", parent key = " << A[parent(i)];
        }
        if (2 * i <= H) {
            std::cout << ", left key = " << A[left(i)];
        }
        if (2 * i + 1 <= H) {
            std::cout << ", right key = " << A[right(i)];
        }
        std::cout << " ";
        std::cout << std::endl;
    }

    return 0;
}
