import java.util.Scanner;

public class Main {
    static int H;
    static int[] A;

    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        H = scanner.nextInt();
        A = new int[H + 1];
        for (int i = 1; i <= H; i++) {
            A[i] = scanner.nextInt();
        }
        buildMaxHeap();
        for (int i = 1; i <= H; i++) {
            System.out.print(A[i] + " ");
        }
    }

    public static void maxHeapify(int[] A, int i) {
        int l = 2 * i;
        int r = 2 * i + 1;
        int largest = i;
        if (l <= H && A[l] > A[i]) {
            largest = l;
        }
        if (r <= H && A[r] > A[largest]) {
            largest = r;
        }
        if (largest != i) {
            swap(A, i, largest);
            maxHeapify(A, largest);
        }
    }

    public static void buildMaxHeap() {
        for (int i = H / 2; i >= 1; i--) {
            maxHeapify(A, i);
        }
    }

    public static void swap(int[] A, int i, int j) {
        int temp = A[i];
        A[i] = A[j];
        A[j] = temp;
    }
}
