class MaxHeap:
    def __init__(self, H):
        self.H = H
        self.heap = [0] * (H + 1)
        self.build_max_heap()

    def build_max_heap(self):
        for i in range(self.H // 2, 0, -1):
            self.max_heapify(i)

    def max_heapify(self, i):
        l = self.left(i)
        r = self.right(i)
        largest = i
        if l <= self.H and self.heap[l] > self.heap[i]:
            largest = l
        if r <= self.H and self.heap[r] > self.heap[largest]:
            largest = r
        if largest != i:
            self.heap[i], self.heap[largest] = self.heap[largest], self.heap[i]
            self.max_heapify(largest)

    def left(self, i):
        return 2 * i

    def right(self, i):
        return 2 * i + 1

    def print_heap(self):
        for i in range(1, self.H + 1):
            print(self.heap[i], end=' ' if i != self.H else '\n')


def main():
    H = int(input())
    A = list(map(int, input().split()))
    for i in range(1, H + 1):
        A[i] = (i, A[i - 1])
    max_heap = MaxHeap(H)
    for i in range(1, H + 1):
        max_heap.heap[i] = A[i][1]
    max_heap.print_heap()


if __name__ == "__main__":
    main()
