#include <stdio.h>
#include <stdlib.h>

#define MAX_SIZE 2000000

typedef struct {
    int data[MAX_SIZE];
    int size;
} PriorityQueue;

void init(PriorityQueue *pq) {
    pq->size = 0;
}

void insert(PriorityQueue *pq, int k) {
    pq->data[pq->size] = k;
    pq->size++;
    for (int i = pq->size - 1; i > 0; i--) {
        if (pq->data[i] > pq->data[(i - 1) / 2]) {
            int temp = pq->data[i];
            pq->data[i] = pq->data[(i - 1) / 2];
            pq->data[(i - 1) / 2] = temp;
            i = (i - 1) / 2;
        } else {
            break;
        }
    }
}

int extractMax(PriorityQueue *pq) {
    if (pq->size == 0) {
        printf("Error: Priority queue is empty.\n");
        exit(1);
    }
    int max = pq->data[0];
    pq->data[0] = pq->data[pq->size - 1];
    pq->size--;
    for (int i = 0; i < pq->size; i++) {
        int largest = i;
        int left = 2 * i + 1;
        int right = 2 * i + 2;
        if (left < pq->size && pq->data[left] > pq->data[largest]) {
            largest = left;
        }
        if (right < pq->size && pq->data[right] > pq->data[largest]) {
            largest = right;
        }
        if (largest != i) {
            int temp = pq->data[i];
            pq->data[i] = pq->data[largest];
            pq->data[largest] = temp;
            i = largest;
        } else {
            break;
        }
    }
    return max;
}

int main() {
    PriorityQueue pq;
    init(&pq);
    char operation[100];
    int k;
    while (1) {
        scanf("%s", operation);
        if (strcmp(operation, "end") == 0) {
            break;
        }
        if (strcmp(operation, "insert") == 0) {
            scanf("%d", &k);
            insert(&pq, k);
        } else if (strcmp(operation, "extract") == 0) {
            printf("%d\n", extractMax(&pq));
        }
    }
    return 0;
}
