import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        String[] line = scanner.nextLine().split(" ");
        double x1 = Double.parseDouble(line[0]);
        double y1 = Double.parseDouble(line[1]);
        double x2 = Double.parseDouble(line[2]);
        double y2 = Double.parseDouble(line[3]);

        int q = Integer.parseInt(scanner.nextLine());
        for (int i = 0; i < q; i++) {
            line = scanner.nextLine().split(" ");
            double x = Double.parseDouble(line[0]);
            double y = Double.parseDouble(line[1]);

            double numerator = (x2 - x1) * (x2 - x) + (y2 - y1) * (y2 - y);
            double denominator = Math.pow(x2 - x1, 2) + Math.pow(y2 - y1, 2);
            double t = numerator / denominator;

            double xProjection = x1 + t * (x2 - x1);
            double yProjection = y1 + t * (y2 - y1);

            System.out.printf("%.10f %.10f%n", xProjection, yProjection);
        }
    }
}
