            double y = Double.parseDouble(line[1]);

            double a = 2 * (x2 - x1);
            double b = 2 * (y2 - y1);
            double c = x2 * x2 - x1 * x1 + y2 * y2 - y1 * y1;
            double x0 = (x1 + x2) / 2;
            double y0 = (y1 + y2) / 2;

            double d = a * (x - x0) + b * (y - y0);
            double e = a * a + b * b;
            double f = c + d * d / e;

            double xRef = x0 + a * d / e;
            double yRef = y0 + b * d / e;

            double numerator = 2 * (x - xRef) + a;
            double denominator = a * a + b * b;
            double xResult = xRef + numerator / denominator * a;
            numerator = 2 * (y - yRef) + b;
            yResult = yRef + numerator / denominator * b;

            System.out.printf("%.10f %.10f%n", xResult, yResult);
        }
    }
}
