#include <iostream>
#include <cmath>

using namespace std;

int main() {
    int x0, y0, x1, y1, q;
    cin >> x0 >> y0 >> x1 >> y1 >> q;

    while (q--) {
        int x2, y2;
        cin >> x2 >> y2;

        int cross_product = (x1 - x0) * (y2 - y0) - (x2 - x0) * (y1 - y0);

        if (cross_product > 0) {
            cout << "COUNTER_CLOCKWISE" << endl;
        } else if (cross_product < 0) {
            cout << "CLOCKWISE" << endl;
        } else {
            if ((x2 - x0) * (x2 - x0) + (y2 - y0) * (y2 - y0) < 1e-9 &&
                (x2 - x1) * (x2 - x1) + (y2 - y1) * (y2 - y1) < 1e-9) {
                cout << "ON_SEGMENT" << endl;
            } else if ((x2 - x0) * (y1 - y0) - (y2 - y0) * (x1 - x0) > -1e-9 &&
                       (x2 - x0) * (y1 - y0) - (y2 - y0) * (x1 - x0) < 1e-9) {
                cout << "ONLINE_BACK" << endl;
            } else {
                cout << "ONLINE_FRONT" << endl;
            }
        }
    }

    return 0;
}
